import React from "react";
import { useState, useEffect } from 'react';
import "../stylesheets/Character.css"

function Characters(){
  const [characters, setCharacters] = useState([]); // Estado para múltiples personajes.

  useEffect(() => {
    fetch("https://rickandmortyapi.com/api/character/1,2,3,500") // URL para obtener más personajes.
      .then((res) => res.json())
      .then((data) => {
        setCharacters(data); // Guardamos los personajes en el estado.
      })
      .catch((error) => {
        console.error("Error fetching data:", error); // Manejo de errores.
      });
  }, []); // Solo ejecuta una vez al cargar el componente. 

  return(
    <div className="character-container">
      <div className="container-text">
      {characters.length > 0 ? (
          characters.map((character) => (
            <div
              key={character.id}
            >
              <p>{character.name}</p>

              <img className="image-character"
                src={character.image}
                alt={character.name}
              />
              <div className="container-text-character">
                <p><strong>Especie:</strong> {character.species}</p>
                <p><strong>Estado:</strong> {character.status}</p>
              </div>
            </div>
          ))
        ) : (
          <p>Cargando datos...</p> // Mensaje mientras se cargan los datos.
        )}
      </div>
    </div>
  )
}

export default Characters;
