import { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [data, setData] = useState(null); // Usa useState para manejar el estado.

  useEffect(() => {
    console.log("entro");

    fetch("https://rickandmortyapi.com/api/character/2") // URL específica del personaje 2.
      .then((res) => res.json()) // Corrige el nombre de "responde" a "res".
      .then((data) => {
        setData(data); // Almacena los datos obtenidos en el estado.
      })
      .catch((error) => {
        console.error("Error fetching data:", error); // Maneja errores de la API.
      });
  }, []); // El array vacío asegura que esto solo se ejecute una vez.

  return (
    <div className="App">
      <div className="Information">
        {data ? ( // Renderiza los datos solo si existen.
          <ul>
            <li><strong>Nombre:</strong> {data.name}</li>
            <li><strong>Especie:</strong> {data.species}</li>
            <li><strong>Estado:</strong> {data.status}</li>
            <li>
              <strong>Imagen:</strong>
              <img src={data.image} alt={data.name} style={{ width: "100px" }} />
            </li>
          </ul>
        ) : (
          <p>Cargando datos...</p> // Muestra un mensaje mientras se cargan los datos.
        )}
      </div>
    </div>
  );
}

export default App;
