import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css";
import Header from "./components/Header";
import Characters from "./components/Character";
import CharacterDetail from "./components/CharacterDetail";
import UnderConstruction from "./components/UnderConstruction";
import Pagination from "./components/Pagination";
import SearchBar from "./components/SearchBar";

function App() {
  const [characters, setCharacters] = useState([]);
  const [filteredCharacters, setFilteredCharacters] = useState([]);
  const [paginatedCharacters, setPaginatedCharacters] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);

  useEffect(() => {
    const fetchAllCharacters = async () => {
      try {
        let allData = [];
        let page = 1;
        let totalPages = 1;

        while (page <= totalPages) {
          const res = await fetch(`https://rickandmortyapi.com/api/character?page=${page}`);
          const data = await res.json();
          allData = [...allData, ...data.results];

          totalPages = data.info.pages;
          page++;
        }

        setCharacters(allData);
        setFilteredCharacters(allData);
      } catch (error) {
        console.error("Error fetching characters:", error);
      }
    };

    fetchAllCharacters();
  }, []);

  return (
    <Router>
      <div className="App">
        <Header />
        <Routes>
          <Route
            path="/"
            element={
              <>
                <div className="app-title-container">
                  <h1 className="app-title">The Rick and Morty App</h1>
                </div>
                <SearchBar
                  characters={characters}
                  setFilteredCharacters={setFilteredCharacters}
                  setPaginatedCharacters={setPaginatedCharacters}
                  setCurrentPage={setCurrentPage}
                />
                <div className="results-count">
                  <p>{filteredCharacters.length} results found</p>
                </div>
                <Characters characters={paginatedCharacters} />
                <Pagination
                  currentPage={currentPage}
                  totalPages={Math.ceil(filteredCharacters.length / 20)}
                  onPageChange={setCurrentPage}
                />
              </>
            }
          />
          <Route path="/character/:id" element={<CharacterDetail />} />
          <Route path="/episodes" element={<UnderConstruction />} />
          <Route path="/locations" element={<UnderConstruction />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
