import { useState, useEffect } from "react";
import "./App.css";
import Character from "./components/Character"; // Importa el componente Character.

function App() {
  const [characters, setCharacters] = useState([]); // Estado para múltiples personajes.

  useEffect(() => {
    const fetchCharacter = async () => {
      try {
        const res = await fetch("https://rickandmortyapi.com/api/character/1,2"); // Solo un personaje
        const data = await res.json();
        setCharacters([data]); // Guarda el personaje en un array para que Character lo reciba igual
      } catch (error) {
        console.error("Error fetching character:", error);
      }
    };

    fetchCharacter();
  }, []);

  return (
    <div className="App">
      <div className="Information">
        {/* Pasamos los personajes como prop */}
        <Character characters={characters} />
      </div>
    </div>
  );
}

export default App;
