import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route, useLocation } from "react-router-dom";
import "./App.css";
import Header from "./components/Header";
import Characters from "./components/Character";
import CharacterDetail from "./components/CharacterDetail";

function UnderConstruction() {
  return (
    <div className="app-title-container">
      <h2 className="app-subtitle">Page Under Construction</h2>
      <p>Estamos trabajando en esta secci√≥n. üöß</p>
    </div>
  );
}

function App() {
  const [characters, setCharacters] = useState([]); // Lista de personajes
  const [filteredCharacters, setFilteredCharacters] = useState([]); // Lista filtrada
  const [searchTerm, setSearchTerm] = useState(""); // Estado de b√∫squeda
  const [currentPage, setCurrentPage] = useState(1); // P√°gina actual
  const [totalPages, setTotalPages] = useState(1); // Total de p√°ginas

  // Obtener la ruta actual para cambiar el subt√≠tulo
  const location = useLocation();

  // Definir los subt√≠tulos dentro de la ruta "/"
  const subtitles = {
    "/": "Characters",
    "/episodes": "Episodes",
    "/locations": "Locations",
  };

  useEffect(() => {
    const fetchCharacters = async () => {
      try {
        const res = await fetch(`https://rickandmortyapi.com/api/character?page=${currentPage}`);
        const data = await res.json();
        setCharacters(data.results); // Lista de personajes de la API
        setFilteredCharacters(data.results); // Inicialmente, la lista filtrada es la misma
        setTotalPages(data.info.pages);
      } catch (error) {
        console.error("Error fetching characters:", error);
      }
    };

    fetchCharacters();
  }, [currentPage]); // Se ejecuta cuando cambia la p√°gina

  // Filtrar personajes en vivo
  useEffect(() => {
    const filtered = characters.filter((character) =>
      character.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCharacters(filtered);
  }, [searchTerm, characters]); // Se actualiza cada vez que cambia el t√©rmino de b√∫squeda o los personajes

  return (
    <Router>
      <div className="App">
        <Header />

        <Routes>
          <Route
            path="/"
            element={
              <>
                      {/* Pantalla con el t√≠tulo */}
                <div className="app-title-container">
                  <h1 className="app-title">The Rick and Morty App</h1>
                </div>
                <div className="app-subtitle-container">
                  <h2 className="app-subtitle">{subtitles[location.pathname] || "Characters"}</h2>
                </div>
                <div className="search-bar-container">
                  <input
                    type="text"
                    placeholder="Buscar personaje..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                  />
                </div>
                <div className="information-characters">
                  <Characters characters={filteredCharacters} />
                </div>
                <div className="pagination">
                  <button 
                    onClick={() => setCurrentPage((prev) => Math.max(prev - 1, 1))}
                    disabled={currentPage === 1}
                  >
                    ‚Üê Anterior
                  </button>
                  <span>P√°gina {currentPage} de {totalPages}</span>
                  <button 
                    onClick={() => setCurrentPage((prev) => Math.min(prev + 1, totalPages))}
                    disabled={currentPage === totalPages}
                  >
                    Siguiente ‚Üí
                  </button>
                </div>
              </>
            }
          />
          <Route path="/character/:id" element={<CharacterDetail />} />
          <Route path="/episodes" element={<UnderConstruction />} />
          <Route path="/locations" element={<UnderConstruction />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
