import { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [characters, setCharacters] = useState([]); // Estado para múltiples personajes.

  useEffect(() => {
    fetch("https://rickandmortyapi.com/api/character/1,2,3") // URL para obtener más personajes.
      .then((res) => res.json())
      .then((data) => {
        setCharacters(data); // Guardamos los personajes en el estado.
      })
      .catch((error) => {
        console.error("Error fetching data:", error); // Manejo de errores.
      });
  }, []); // Solo ejecuta una vez al cargar el componente.

  return (
    <div className="App">
      <div className="Information">
        {characters.length > 0 ? ( // Renderiza la lista si hay personajes.
          <ul>
            {characters.map((character) => ( // Mapea cada personaje.
              <li key={character.id}>
                <strong>Nombre:</strong> {character.name}<br />
                <strong>Especie:</strong> {character.species}<br />
                <strong>Estado:</strong> {character.status}<br />
                <img src={character.image} alt={character.name} style={{ width: "100px" }} /><br />
              </li>
            ))}
          </ul>
        ) : (
          <p>Cargando datos...</p> // Mensaje mientras se cargan los datos.
        )}
      </div>
    </div>
  );
}

export default App;
