import { useState, useEffect } from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import "./App.css";
import Header from "./components/Header";
import Characters from "./components/Character";
import CharacterDetail from "./components/CharacterDetail";
import UnderConstruction from "./components/UnderConstruction";

function App() {
  const [characters, setCharacters] = useState([]); // Lista de personajes
  const [filteredCharacters, setFilteredCharacters] = useState([]); // Lista filtrada
  const [searchTerm, setSearchTerm] = useState(""); // Estado de búsqueda
  const [currentPage, setCurrentPage] = useState(1); // Página actual
  const [totalPages, setTotalPages] = useState(1); // Total de páginas


  //Filtar personajes
  const [allCharacters, setAllCharacters] = useState([]); // Todos los personajes


  useEffect(() => {
    const fetchAllCharacters = async () => {
      try {
        let allData = [];
        let page = 1;
        let totalPages = 1; // Se actualizará después de la primera llamada

        while (page <= totalPages) {
          const res = await fetch(`https://rickandmortyapi.com/api/character?page=${page}`);
          const data = await res.json();

          allData = [...allData, ...data.results];

          totalPages = data.info.pages; // Obtiene el total de páginas
          page++;
        }

        setAllCharacters(allData); // Guarda todos los personajes
        setCharacters(allData.slice(0, 30)); // Muestra solo los primeros 30 personajes
        setFilteredCharacters(allData.slice(0, 30)); // Inicialmente, los filtrados son los mismos
      } catch (error) {
        console.error("Error fetching characters:", error);
      }
    };

    fetchAllCharacters();
  }, []);


  useEffect(() => {
  const filtered = allCharacters.filter((character) =>
    character.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  setFilteredCharacters(filtered.slice(0, 30)); // Mostrar los primeros 30 resultados filtrados
}, [searchTerm, allCharacters]);

const handlePageChange = (newPage) => {
  const startIndex = (newPage - 1) * 30;
  const endIndex = startIndex + 30;
  setCharacters(filteredCharacters.slice(startIndex, endIndex));
  setCurrentPage(newPage);
};

  return (
    <Router>
      <div className="App">
        <Header />

        <Routes>
          <Route
            path="/"
            element={
              <>
                      {/* Pantalla con el título */}
                <div className="app-title-container">
                  <h1 className="app-title">The Rick and Morty App</h1>
                </div>
                <div className="app-subtitle-container">
                  <h2 className="app-subtitle">Characters</h2>
                </div>
                <div className="search-bar-container">
                  <input
                    type="text"
                    placeholder="Buscar personaje..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="search-bar"
                  />
                </div>
                <Characters characters={filteredCharacters} />
                <div className="pagination">
                  <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
                    ← Anterior
                  </button>
                  <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage >= Math.ceil(filteredCharacters.length / 30)}>
                    Siguiente →
                  </button>
                </div>
              </>
            }
          />
          <Route path="/character/:id" element={<CharacterDetail />} />
          <Route path="/episodes" element={<UnderConstruction />} />
          <Route path="/locations" element={<UnderConstruction />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
